import React, { Component } from 'react';
import { Route, NavLink } from "react-router-dom";
import Product from './product';

class Products extends Component {

    constructor(props){
        super(props);
        this.state = {
            id: 1,
            name: '',
            img: '',
            price: '',
        }
    }

    HandleChangeProduct(product){
        this.setState({
            id: product.id,
            name: product.name,
            //img: product.img,
            price: product.price
        })   
    }

    render() {
        var products = [
            {
                id : 1,
                slug : 'iphone-x',
                name : 'Iphone X',
                price : 30000000
            },
            {
                id : 2,
                slug : 'samsung-galaxy-s7',
                name : 'SamSung Galaxy S7',
                price : 13000000
            },
            {
                id : 3,
                slug : 'oppo-f1s',
                name : 'Oppo F1s',
                price : 10000000
            }
        ];

        var { match } = this.props;
        var url = match.url;        

        // var result = products.map((product, index) => {
        //     return (
        //         <NavLink key={index} to={`${url}/${product.slug}`} id={product.id}>
        //             <li className="list-group-item">
        //                 {product.id} - {product.name} - {product.price}
        //             </li>
        //         </NavLink>
        //     );
        // });

        var result = products.map((product, index) => {
            return (
                <tr key={index}>
                    <td onClick={this.HandleChangeProduct(product)}>{product.id}</td>
                    <td>{product.name}</td>
                    <td>{product.price}</td>
                </tr>
            );
        });
        
        return (
            <div className="container">
                <h1>Day la danh sach san pham</h1>
                
                <div>

                     <table className = "table table-bordered table-hover">
                        <thead>
                              <tr>
                                <th>ID</th>
                                <th>Tên Sản Phẩm</th>
                                <th>Giá</th>
                            </tr>
                        </thead>
                        <tbody>
                              {result !== '' ? result : ''}
                        </tbody>
                    </table>
                    {/*<ul className="list-group">
                    </ul>*/}
                    
                </div>
                
               {/* <div className="row">
                    <Route path={`${url}/:name`} component={Product} />
                </div>*/}
                
                
            </div>
        );
    }
}

export default Products;
