import React, { Component } from 'react';
import { Route, NavLink } from "react-router-dom";
import Product from './product';
import pic1 from "./images/102180248.jpg"
import pic2 from "./images/102180254.jpg"
import pic3 from "./images/102180268.jpg"
import pic4 from "./images/102180271.jpg"

class Products extends Component {

    constructor(props){
        super(props);
        this.state = {
            id: 0,
            name: '',
            img: '',
            classc: '',
        }
    }

    HandleChangeProduct(product){
        console.log('a');
        this.setState({
            id: product.id,
            name: product.name,
            img: product.img,
            classc: product.classc
        })   
    }

    render() {
        var products = [
            {
                id : 1,
                slug : 'le-thi-luu-hieu',
                name : 'Lê Thị Lưu Hiếu',                
                img: pic1 ,
                classc : '18N16B'
            },
            {
                id : 2,
                slug : 'thai-thi-thu-loan',
                name : 'Thái Thị Thu Loan',                
                img : pic2, 
                classc : '18N16A'
            },
            {
                id : 3,
                slug : 'tran-xuan-phuc',
                name : 'Trần Xuân Phúc',
                img : pic3, 
                classc : '18N16B'
            }, 
            {
                id : 4,
                slug : 'nguyen-dang-thong',
                name : 'Nguyễn Đăng Thông',
                img : pic4, 
                classc : '18N16B'
            }            
        ];
     
        const {id, name, img, classc} = this.state;

        // var result = products.map((product, index) => {
        //     return (
        //         <NavLink key={index} to={`${url}/${product.slug}`} id={product.id}>
        //             <li className="list-group-item">
        //                 {product.id} - {product.name} - {product.classc}
        //             </li>
        //         </NavLink>
        //     );
        // });

        var result = products.map((product, index) => {
            return (
                <tr key={index}>
                    <td onClick={() => this.HandleChangeProduct(product)}>{product.id}</td>
                    <td>{product.name}</td>
                    <td>{product.classc}</td>
                </tr>
            );
        });

        return (
            <div className="container">
                <h1>Day la danh sach san pham</h1>
                
                <div>

                     <table className = "table table-bordered table-hover">
                        <thead>
                              <tr>
                                <th>ID</th>
                                <th>Tên Sinh Viên</th>
                                <th>Lớp</th>
                            </tr>
                        </thead>
                        <tbody>
                              {result !== '' ? result : ''}
                        </tbody>
                    </table>
                    {/*<ul className="list-group">
                    </ul>*/}
                    
                </div>
                <Product id={id} name={name} img={img} classc={classc} />
               {/* <div className="row">
                    <Route path={`${url}/:name`} component={Product} />
                </div>*/}
                
                
            </div>
        );
    }
}

export default Products;
